import React, {useEffect, useState} from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

const endpoint = 'http://localhost:8000/api'
const ShowProducts = () => {
    const [ products, setProducts ] = useState([])
    const [search, setSearch] = useState('')

    const searcher = (e) => {
        setSearch(e.target.value)
    }

    const results = !search ? products : products.filter((dato)=> dato.description.toLowerCase().includes(search.toLocaleLowerCase()))

    useEffect ( ()=> {
        getAllProducts()
    }, [])

    const getAllProducts = async () => {
    const response = await axios.get(`${endpoint}/products`)
    setProducts (response.data)
    }

    const deleteProduct = async (id) => {
       await axios.delete(`${endpoint}/product/${id}`)
       getAllProducts()
    }


    return (
    <div>
        <input value={search} onChange={searcher} type='text' placeholder='Search' className='form-control' ></input>
        
        <div className='d-grid gap-2'>
            <Link to = "/create" className='btn btn-success btn-lg mt-2 mb-2 text-white'>Create</Link>
        </div>

        <table className='table table-striped'>
            <thead className='bg-primary text-white'>
                <tr>
                    <th>Description</th>
                    <th>Price</th>
                    <th>stock</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                { results.map( (product) => (
                    <tr key={product.id}>
                        <td> {product.description} </td>
                        <td> {product.price} </td>
                        <td> {product.stock} </td>
                        <td> 
                            <Link to={`/edit/${product.id}`} className='btn btn-warning'>Edit</Link>
                            <button onClick={ () => deleteProduct(product.id) } className='btn btn-danger'>Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    </div>
  )
}

export default ShowProducts
